export declare const ModalStyle = "\n    * {\n        box-sizing: border-box;\n    }\n\n    .popup {\n        padding: 16px;\n        position: fixed;\n        top: 50%;\n        left: 50%;\n        transform: translate(-50%, -50%);\n        background: #FFFFFF;\n        border-radius: 16px;\n        box-shadow: 0px 6px 12px rgba(17, 24, 39, 0.11);\n        display: flex;\n        flex-direction: column;\n        min-width: 493px;\n    }\n\n    .header {\n        display: flex;\n        justify-content: space-between;\n        align-items: center;\n        width: 100%;\n    }\n\n    .title {\n        margin: 0;\n        font-family: \"Inter\";\n        font-weight: 700;\n        font-size: 24px;\n        line-height: 32px;\n        color: #111827;\n        letter-spacing: -0.02em;\n    }\n    \n    .close-button {\n        cursor: pointer;\n    }";
